// Single package install: npm install @farajabien/slug-store
import { saveUserState, loadUserState } from '@farajabien/slug-store'

// 1. Store in user profile (one-time setup)
const userState = { theme: 'dark', preferences: {...} }
const { slug } = await saveUserState(userState)

await supabase.from('profiles').insert({
  user_id: user.id,
  app_state: slug  // Compressed, encrypted state
})

// 2. Load user state (every page load)
const { data: profile } = await supabase
  .from('profiles')
  .select('app_state')
  .eq('user_id', user.id)
  .single()

const appData = await loadUserState(profile.app_state)

implementation.react:
// Complete SaaS app with ONE package install
import { create } from '@farajabien/slug-store'

const useSaaSStore = create((set, get) => ({
  // User state
  user: { name: '', email: '', plan: 'free' },
  onboarding: { step: 1, completed: false },

  // App state
  projects: [],
  currentProject: null,
  workflows: [],

  // Business logic
  createProject: (name) => set(state => ({
    projects: [...state.projects, {
      id: Date.now(),
      name,
      createdAt: new Date().toISOString()
    }]
  })),

  upgradeToProPlan: () => set({ 
    user: { ...get().user, plan: 'pro' } 
  })
}), { compress: true, key: 'saas' })

// Hybrid approach: Simple auth + powerful state persistence
import { create, createShareableUrl } from '@farajabien/slug-store'

const useWorkspaceStore = create((set, get) => ({
  // This state lives in URLs - infinitely scalable
  workspace: { name: '', settings: {} },
  dashboard: { widgets: [], layout: 'grid' },
  filters: { dateRange: 'last-7-days' },

  // User data comes from your auth provider (Supabase, Auth0, etc.)
  // App state automatically syncs to URLs

  updateDashboard: (widgets) => set({ dashboard: { widgets } }),
  applyFilters: (filters) => set({ filters }),

  // Collaboration via URL sharing
  shareWorkspace: async () => {
    const url = await createShareableUrl(get())
    return url // Share this with team members
  }
}), { 
  compress: true, 
  encrypt: true, 
  password: userSessionToken, // From your auth provider
  key: 'workspace' 
})

// Enterprise-grade state management
const useEnterpriseStore = create((set, get) => ({
  // Complex business state in URLs
  organization: { departments: [], policies: [] },
  processes: { approvals: [], workflows: [] },
  compliance: { regulations: [], audits: [] },

  // Advanced business logic
  initiateApproval: (request) => set(state => ({
    processes: {
      ...state.processes,
      approvals: [...state.processes.approvals, {
        id: generateId(),
        request,
        status: 'pending',
        chain: state.organization.approvalChain,
        createdAt: new Date().toISOString()
      }]
    }
  })),

  // Compliance tracking
  addAuditEvent: (event) => set(state => ({
    compliance: {
      ...state.compliance,
      audits: [...state.compliance.audits, event]
    }
  })),

  // Enterprise sharing with encryption
  shareSecureState: async () => {
    const url = await get().getShareableUrl()
    // URLs are encrypted with enterprise-grade security
    return url
  }
}), { 
  compress: true, 
  encrypt: true, 
  password: enterpriseSecurityKey,
  key: 'enterprise',
  debounceMs: 500 // Handle high-frequency updates
})

import { create } from '@farajabien/slug-store'

const useCanvasStore = create((set) => ({
  elements: [],
  viewport: { zoom: 1, pan: { x: 0, y: 0 } },
  selectedTool: 'select',
  addElement: (element) => set(state => ({ 
    elements: [...state.elements, element] 
  })),
  updateViewport: (viewport) => set({ viewport })
}), { compress: true, debounceMs: 300 })

const useDiagramStore = create((set) => ({
  nodes: [],
  edges: [],
  layout: 'manual',
  addNode: (node) => set(state => ({ nodes: [...state.nodes, node] })),
  connectNodes: (from, to, label) => set(state => ({
    edges: [...state.edges, { from, to, label }]
  }))
}), { compress: true })

const useCartStore = create((set, get) => ({
  items: [],
  promoCode: null,
  addItem: (item) => set(state => ({
    items: [...state.items, { ...item, quantity: 1 }]
  })),
  removeItem: (id) => set(state => ({
    items: state.items.filter(item => item.id !== id)
  })),
  getTotal: () => get().items.reduce((sum, item) => sum + item.price * item.quantity, 0)
}), { compress: true, key: 'cart' })

const useTodoStore = create((set) => ({
  todos: [],
  filter: 'all', // 'all', 'active', 'completed'
  selectedProject: null,
  addTodo: (text, project = 'inbox') => set(state => ({
    todos: [...state.todos, {
      id: Date.now().toString(),
      text,
      project,
      done: false,
      priority: 'medium',
      createdAt: new Date().toISOString()
    }]
  })),
  toggleTodo: (id) => set(state => ({
    todos: state.todos.map(todo => 
      todo.id === id ? { ...todo, done: !todo.done } : todo
    )
  }))
}), { compress: true })

const useKanbanStore = create((set) => ({
  columns: [
    { id: 'todo', title: 'To Do', cards: [] },
    { id: 'doing', title: 'In Progress', cards: [] },
    { id: 'done', title: 'Done', cards: [] }
  ],
  cards: {},
  addCard: (columnId, title) => set(state => {
    const cardId = Date.now().toString()
    return {
      cards: { ...state.cards, [cardId]: { id: cardId, title } },
      columns: state.columns.map(col => 
        col.id === columnId 
          ? { ...col, cards: [...col.cards, cardId] }
          : col
      )
    }
  })
}), { compress: true, key: 'board' })

