const useFinanceStore = create((set) => ({
  transactions: [],
  budgets: {},
  categories: ['food', 'transport', 'entertainment', 'utilities'],
  view: 'monthly',
  addTransaction: (transaction) => set(state => ({
    transactions: [...state.transactions, {
      ...transaction,
      id: Date.now().toString(),
      date: new Date().toISOString()
    }]
  })),
  setBudget: (category, amount) => set(state => ({
    budgets: { ...state.budgets, [category]: amount }
  }))
}), { encrypt: true, password: userKey, key: 'finance' })

const useHabitStore = create((set, get) => ({
  habits: [],
  completions: {},
  view: 'week',
  addHabit: (name, goal = 'daily') => set(state => ({
    habits: [...state.habits, {
      id: Date.now().toString(),
      name,
      goal,
      streak: 0,
      createdAt: new Date().toISOString()
    }]
  })),
  markComplete: (habitId, date = new Date().toISOString().split('T')[0]) => {
    const state = get()
    const dayCompletions = state.completions[date] || {}
    set({
      completions: {
        ...state.completions,
        [date]: { ...dayCompletions, [habitId]: true }
      }
    })
  }
}), { compress: true, key: 'habits' })

const useMealPlanStore = create((set) => ({
  weekPlan: {},
  recipes: [],
  shoppingList: [],
  dietaryRestrictions: [],
  setPlan: (day, meal, recipe) => set(state => ({
    weekPlan: {
      ...state.weekPlan,
      [day]: { ...state.weekPlan[day], [meal]: recipe }
    }
  })),
  addToShoppingList: (item) => set(state => ({
    shoppingList: [...state.shoppingList, item]
  })),
  generateShoppingList: () => {
    // Auto-generate from weekly plan
  }
}), { compress: true, key: 'meals' })

const useWorkoutStore = create((set) => ({
  currentWorkout: null,
  workoutHistory: [],
  routines: [],
  settings: { weightUnit: 'lbs', restTimer: 60 },
  startWorkout: (routine) => set({ currentWorkout: routine }),
  addSet: (exerciseIndex, set) => set(state => ({
    currentWorkout: {
      ...state.currentWorkout,
      exercises: state.currentWorkout.exercises.map((ex, i) => 
        i === exerciseIndex 
          ? { ...ex, sets: [...ex.sets, set] }
          : ex
      )
    }
  })),
  finishWorkout: () => set(state => ({
    workoutHistory: [...state.workoutHistory, {
      ...state.currentWorkout,
      completedAt: new Date().toISOString()
    }],
    currentWorkout: null
  }))
}), { compress: true, key: 'workout' })
